import { Request, Response } from "express";
import OpenAI from "openai";
import "dotenv/config";

const client = new OpenAI({
  baseURL: "https://api.studio.nebius.com/v1/",
  apiKey: process.env.NEBIUS_API_KEY,
});

const generateImageController = async ( req: Request, res: Response ): Promise<void> => {
  const { prompt } = req.body;
  try {
    const response = await client.images.generate({
      model: "black-forest-labs/flux-schnell",
      response_format: "b64_json",
      prompt: prompt,
      ...{
        response_extension: "png",
        width: 1024,
        height: 1024,
        num_inference_steps: 4,
        negative_prompt: "",
        seed: -1,
      },
    });

    const image = response.data[0].b64_json;
    
    // replace the hardcoded image with the one from the response
    res.status(200).json({
      success: true,
      photo: image
      // below one is a hard coded image for testing purpose 
      // photo: '',
    });
  } catch (err) {
    res.status(500).json({
      success: false,
      message: "internal server error",
    });
  }
};

export { generateImageController };
